import {Link} from 'react-router-dom';
import { useState } from 'react';

export default function Navbar(){
    var clss;

    const [scrollTop, setScrollTop] = useState(0);

    if(scrollTop>100){
        clss="active";
    }
    else{
        clss="hidden";
    }

    const handleScroll = event =>{
        console.log("scroll: "+scrollTop);
        if(scrollTop>0){
            clss="active";
        }
        else{
            clss="hidden";
        }
        
    }
    return(
        
        <div className={`navbar ${clss}`}>
        {/* <div className='navbar'> */}
            <nav className="navbar_nav" onScroll={handleScroll()}>
                <div className="nav_name">
                    <h2>MyBlog</h2>
                </div>
                <div className="nav_center">
                    <Link to=''>Link1</Link>
                    <Link to=''>Link2</Link>
                    <Link to=''>Link3</Link>
                    <Link to=''>Link4</Link>
                </div>
                <div className='nav_right'>
                    <h3>icon</h3>
                </div>
            </nav>
        </div>

    );
}
